mapper.sql:
where 1=1


    and project ${projectOperator} #{project}


    and plan ${planOperator} #{plan}


    and solution ${solutionOperator} #{solution}


    and isqualified ${isqualifiedOperator} #{isqualified}


    and clausecontent ${clausecontentOperator} #{clausecontent}


    and clausenum ${clausenumOperator} #{clausenum}


    and office ${officeOperator} #{office}


    and dept ${deptOperator} #{dept}


    and engineerid ${engineeridOperator} #{engineerid}


    and responsiblemanid ${responsiblemanidOperator} #{responsiblemanid}


    and task_id ${taskIdOperator} #{taskId}


    and proc_inst_id ${procInstIdOperator} #{procInstId}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from BUS_NOTICECHECK_PROCESS


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
