mapper.sql:
where 1=1


    and end_time ${endTimeOperator} #{endTime}


    and end_time >= #{endTime1}


    and end_time <= #{endTime2}


    and create_time ${createTimeOperator} #{createTime}


    and create_time >= #{createTime1}


    and create_time <= #{createTime2}


    and process_status ${processStatusOperator} #{processStatus}


    and now_user_id ${nowUserIdOperator} #{nowUserId}


    and now_uesr_name ${nowUesrNameOperator} #{nowUesrName}


    and last_user_id ${lastUserIdOperator} #{lastUserId}


    and last_user_name ${lastUserNameOperator} #{lastUserName}


    and create_user_id ${createUserIdOperator} #{createUserId}


    and standard_id ${standardIdOperator} #{standardId}


    and process_type ${processTypeOperator} #{processType}


    and process_number ${processNumberOperator} #{processNumber}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from BUS_PROCESS


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
