mapper.sql:
where 1=1


    and isagree ${isagreeOperator} #{isagree}


    and res_flag ${resFlagOperator} #{resFlag}


    and proc_inst_id ${procInstIdOperator} #{procInstId}


    and parent_id ${parentIdOperator} #{parentId}


    and reason_explain ${reasonExplainOperator} #{reasonExplain}


    and prove_material ${proveMaterialOperator} #{proveMaterial}


    and res_result ${resResultOperator} #{resResult}


    and engineer ${engineerOperator} #{engineer}


    and project_manager ${projectManagerOperator} #{projectManager}


    and dept_name ${deptNameOperator} #{deptName}


    and res_name ${resNameOperator} #{resName}


    and res_num ${resNumOperator} #{resNum}


    and res_id ${resIdOperator} #{resId}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from BUS_RES_ANALY_PROCESS


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
