mapper.sql:
where 1=1


    and modify_time ${modifyTimeOperator} #{modifyTime}


    and modify_time >= #{modifyTime1}


    and modify_time <= #{modifyTime2}


    and creation_time ${creationTimeOperator} #{creationTime}


    and creation_time >= #{creationTime1}


    and creation_time <= #{creationTime2}


    and valid_flag ${validFlagOperator} #{validFlag}


    and creation_user ${creationUserOperator} #{creationUser}


    and duty_unit ${dutyUnitOperator} #{dutyUnit}


    and replace_stand_num ${replaceStandNumOperator} #{replaceStandNum}


    and remarks ${remarksOperator} #{remarks}


    and put_year ${putYearOperator} #{putYear}


    and put_time ${putTimeOperator} #{putTime}


    and put_time >= #{putTime1}


    and put_time <= #{putTime2}


    and issue_time ${issueTimeOperator} #{issueTime}


    and issue_time >= #{issueTime1}


    and issue_time <= #{issueTime2}


    and stand_name ${standNameOperator} concat(concat('%',#{standName}),'%')


    and stand_year ${standYearOperator} #{standYear}


    and stand_number ${standNumberOperator} concat(concat('%',#{standNumber}),'%')


    and stand_code ${standCodeOperator} #{standCode}


    and classify_code ${classifyCodeOperator} concat(concat('%',#{classifyCode}),'%')


    and bus_stand_subclass ${busStandSubclassOperator} concat(concat('%',#{busStandSubclass}),'%')


    and bus_stand_classify ${busStandClassifyOperator} #{busStandClassify}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from SAR_BUS_SAR_COMPILE


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}

select  from SAR_BUS_SAR_COMPILE
where stand_number = #{standNumber} and valid_flag = #{validFlag}

    and id != #{id}
