mapper.sql:
where 1=1


    and modify_time ${modifyTimeOperator} #{modifyTime}


    and modify_time >= #{modifyTime1}


    and modify_time <= #{modifyTime2}


    and creation_time ${creationTimeOperator} #{creationTime}


    and creation_time >= #{creationTime1}


    and creation_time <= #{creationTime2}


    and valid_flag ${validFlagOperator} #{validFlag}


    and approval_draft_time ${approvalDraftTimeOperator} #{approvalDraftTime}


    and approval_draft_time >= #{approvalDraftTime1}


    and approval_draft_time <= #{approvalDraftTime2}


    and start_flow_time ${startFlowTimeOperator} #{startFlowTime}


    and start_flow_time >= #{startFlowTime1}


    and start_flow_time <= #{startFlowTime2}


    and review_modify_time ${reviewModifyTimeOperator} #{reviewModifyTime}


    and review_modify_time >= #{reviewModifyTime1}


    and review_modify_time <= #{reviewModifyTime2}


    and review_meet_time ${reviewMeetTimeOperator} #{reviewMeetTime}


    and review_meet_time >= #{reviewMeetTime1}


    and review_meet_time <= #{reviewMeetTime2}


    and review_submit_time ${reviewSubmitTimeOperator} #{reviewSubmitTime}


    and review_submit_time >= #{reviewSubmitTime1}


    and review_submit_time <= #{reviewSubmitTime2}


    and finish_review_time ${finishReviewTimeOperator} #{finishReviewTime}


    and finish_review_time >= #{finishReviewTime1}


    and finish_review_time <= #{finishReviewTime2}


    and compilers_user ${compilersUserOperator} #{compilersUser}


    and review_level ${reviewLevelOperator} #{reviewLevel}


    and compile_unit ${compileUnitOperator} concat(concat('%',#{compileUnit}),'%')


    and replace_stand_num ${replaceStandNumOperator} concat(concat('%',#{replaceStandNum}),'%')


    and make_revison_type ${makeRevisonTypeOperator} concat(concat('%',#{makeRevisonType}),'%')


    and stand_name ${standNameOperator} concat(concat('%',#{standName}),'%')


    and id ${idOperator} #{id}

mapper.select:
	select ,reviewSubmitDays,submitDelayDays,planReleaseDays,releaseDelayDays from
		(select tmp_tb.* , rownum rn from
			(select ,
              (
                  select Round(TO_NUMBER(SAR_BUSS_STAND_PLAN.FINISH_REVIEW_TIME- SYSDATE))
                  from dual
              ) as reviewSubmitDays,
              (
                  select Round(TO_NUMBER(SAR_BUSS_STAND_PLAN.REVIEW_SUBMIT_TIME- SAR_BUSS_STAND_PLAN.FINISH_REVIEW_TIME))
                  from dual
              ) as submitDelayDays,
              (
                  select Round(TO_NUMBER(SAR_BUSS_STAND_PLAN.APPROVAL_DRAFT_TIME- SYSDATE))
                  from dual
              ) as planReleaseDays,
              (
                  select Round(TO_NUMBER(SAR_BUSS_STAND_PLAN.START_FLOW_TIME- SAR_BUSS_STAND_PLAN.APPROVAL_DRAFT_TIME))
                  from dual
              ) as releaseDelayDays
              from SAR_BUSS_STAND_PLAN


			 	${pager.orderCondition}

			) tmp_tb where rownum <= ${pager.endIndex})
			where rn >= ${pager.startIndex}
