mapper.sql:
where 1=1


    and modify_time ${modifyTimeOperator} #{modifyTime}


    and modify_time >= #{modifyTime1}


    and modify_time <= #{modifyTime2}


    and creation_time ${creationTimeOperator} #{creationTime}


    and creation_time >= #{creationTime1}


    and creation_time <= #{creationTime2}


    and valid_time ${validTimeOperator} #{validTime}


    and put_user ${putUserOperator} #{putUser}


    and citation_user ${citationUserOperator} #{citationUser}


    and responsible_unit ${responsibleUnitOperator} #{responsibleUnit}


    and stand_file ${standFileOperator} #{standFile}


    and tags ${tagsOperator} #{tags}


    and stand_status ${standStatusOperator} #{standStatus}


    and replaced_stand_num ${replacedStandNumOperator} #{replacedStandNum}


    and replace_stand_num ${replaceStandNumOperator} #{replaceStandNum}


    and quote_stand ${quoteStandOperator} #{quoteStand}


    and first_put_time ${firstPutTimeOperator} #{firstPutTime}


    and first_put_time >= #{firstPutTime1}


    and first_put_time <= #{firstPutTime2}


    and put_year ${putYearOperator} #{putYear}


    and put_year >= #{putYear1}


    and put_year <= #{putYear2}


    and put_time ${putTimeOperator} #{putTime}


    and put_time >= #{putTime1}


    and put_time <= #{putTime2}


    and issue_time ${issueTimeOperator} #{issueTime}


    and issue_time >= #{issueTime1}


    and issue_time <= #{issueTime2}


    and energy_kind ${energyKindOperator} #{energyKind}


    and apply_arctic ${applyArcticOperator} #{applyArctic}


    and stand_en_name ${standEnNameOperator} #{standEnName}


    and stand_name ${standNameOperator} #{standName}


    and stand_code ${standCodeOperator} #{standCode}


    and classify_code ${classifyCodeOperator} #{classifyCode}


    and stand_subclass ${standSubclassOperator} #{standSubclass}


    and stand_genera ${standGeneraOperator} #{standGenera}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from SAR_BUSSIONESS_STAND


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}

mapper.sql:

left join TS_DICTYPE dicstandStatus on  (dicadoptExtent.dic_type_code = SAR_BUSSIONESS_STAND.STAND_STATUS  and dicadoptExtent.dic_id is not null)
where 1=1



        and classify_code like  concat(concat('%',#{classifyCode}),'%')


        and stand_code like  concat(concat('%',#{standCode}),'%')


        and stand_name like  concat(concat('%',#{standName}),'%')



        and stand_subclass like  concat(concat('%',#{standSubclass}),'%')


        and stand_genera like  concat(concat('%',#{standGenera}),'%')


        and to_char(put_time,'YYYY-MM-DD') >= #{putTime1}


        and to_char(put_time,'YYYY-MM-DD') <= #{putTime2}


        and to_char(issue_time,'YYYY-MM-DD') >= #{issueTime1}


        and to_char(issue_time,'YYYY-MM-DD') <= #{issueTime2}


        and responsible_unit = #{responsibleUnit}

mapper.select:
select * from (select tmp_tb.* , rownum rn from (select , ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_BUSS_STAND_VAL.property_type ) FROM SAR_BUSS_STAND_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_BUSS_STAND_VAL.property_val and TS_DICTYPE.dic_id is not null) WHERE SAR_BUSS_STAND_VAL.stand_id = SAR_BUSSIONESS_STAND.id AND SAR_BUSS_STAND_VAL.property_type = 'APPLY_ARCTIC' ) AS applyArcticShow, ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_BUSS_STAND_VAL.property_type ) FROM SAR_BUSS_STAND_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_BUSS_STAND_VAL.property_val and TS_DICTYPE.dic_id is not null) WHERE SAR_BUSS_STAND_VAL.stand_id = SAR_BUSSIONESS_STAND.id AND SAR_BUSS_STAND_VAL.property_type = 'ENERGY_KIND' ) AS energyKindShow from SAR_BUSSIONESS_STAND
${pager.orderCondition}
) tmp_tb where rownum <= ${pager.endIndex}) where rn >= ${pager.startIndex}
