mapper.sql:
where 1=1


    and modify_time ${modifyTimeOperator} #{modifyTime}


    and modify_time >= #{modifyTime1}


    and modify_time <= #{modifyTime2}


    and creation_time ${creationTimeOperator} #{creationTime}


    and creation_time >= #{creationTime1}


    and creation_time <= #{creationTime2}


    and valid_flag ${validFlagOperator} #{validFlag}


    and creation_user ${creationUserOperator} #{creationUser}


    and relevance_file ${relevanceFileOperator} #{relevanceFile}


    and opinion_file ${opinionFileOperator} #{opinionFile}


    and laws_file ${lawsFileOperator} #{lawsFile}


    and link_uri ${linkUriOperator} #{linkUri}


    and responsible_unit ${responsibleUnitOperator} #{responsibleUnit}


    and apply_auth ${applyAuthOperator} concat(concat('%',#{applyAuth}),'%')


    and energy_kind ${energyKindOperator} concat(concat('%',#{energyKind}),'%')


    and apply_arctic ${applyArcticOperator} concat(concat('%',#{applyArctic}),'%')


    and replace_laws_num ${replaceLawsNumOperator} #{replaceLawsNum}


    and put_time ${putTimeOperator} #{putTime}


    and put_time >= #{putTime1}


    and put_time <= #{putTime2}


    and issue_time ${issueTimeOperator} #{issueTime}


    and issue_time >= #{issueTime1}


    and issue_time <= #{issueTime2}


    and laws_state ${lawsStateOperator} #{lawsState}


    and issue_unit ${issueUnitOperator} #{issueUnit}


    and laws_name ${lawsNameOperator} concat(concat('%',#{lawsName}),'%')


    and laws_number ${lawsNumberOperator} concat(concat('%',#{lawsNumber}),'%')


    and laws_property ${lawsPropertyOperator} #{lawsProperty}


    and country ${countryOperator} #{country}


    and laws_type ${lawsTypeOperator} #{lawsType}


    and id ${idOperator} #{id}

mapper.select:
	select ,propertyName,stateName from
		(select tmp_tb.* , rownum rn from
			(select SAR_LAWS_INFO.*,dic1.dic_type_name as propertyName,dic2.dic_type_name as stateName
              from SAR_LAWS_INFO
              left join TS_DICTYPE dic1 on SAR_LAWS_INFO.laws_property = dic1.dic_type_code
              left join TS_DICTYPE dic2 on SAR_LAWS_INFO.laws_state = dic2.dic_type_code

              and SAR_LAWS_INFO.valid_flag = 0

			 	${pager.orderCondition}

			) tmp_tb where rownum <= ${pager.endIndex})
			where rn >= ${pager.startIndex}
