mapper.select:
select * from (select tmp_tb.* , rownum rn from (select , ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_PRODUCT_VAL.property_type ) FROM SAR_PRODUCT_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_PRODUCT_VAL.PROPERTY_VALUE and TS_DICTYPE.dic_id is not null) WHERE SAR_PRODUCT_VAL.PRODUCT_ID = SAR_PRODUCT_INFO.id AND SAR_PRODUCT_VAL.property_type = 'ENERGY_KIND' ) AS energyKindShow, ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_PRODUCT_VAL.property_type ) FROM SAR_PRODUCT_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_PRODUCT_VAL.PROPERTY_VALUE and TS_DICTYPE.dic_id is not null) WHERE SAR_PRODUCT_VAL.PRODUCT_ID = SAR_PRODUCT_INFO.id AND SAR_PRODUCT_VAL.property_type = 'PRODUCT_TYPE' ) AS productTypeShow from SAR_PRODUCT_INFO
${pager.orderCondition}
) tmp_tb where rownum <= ${pager.endIndex}) where rn >= ${pager.startIndex}

