mapper.sql:
where 1=1


    and modify_time ${modifyTimeOperator} #{modifyTime}


    and modify_time >= #{modifyTime1}


    and modify_time <= #{modifyTime2}


    and creation_time ${creationTimeOperator} #{creationTime}


    and creation_time >= #{creationTime1}


    and creation_time <= #{creationTime2}


    and creation_user ${creationUserOperator} #{creationUser}


    and car_mode_file ${carModeFileOperator} #{carModeFile}


    and energy_kind ${energyKindOperator} #{energyKind}


    and product_type ${productTypeOperator} #{productType}


    and product_brand ${productBrandOperator} #{productBrand}


    and product_name ${productNameOperator} #{productName}


    and product_set ${productSetOperator} #{productSet}


    and prodect_code ${prodectCodeOperator} #{prodectCode}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from SAR_PRODUCT_INFO


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}

