mapper.sql:
left join TS_DICTYPE dicountry on (dicountry.dic_type_code = SAR_STANDARDS_INFO.country  and dicountry.dic_id is not null and dicountry.valid_flag = 0)
left join TS_DICTYPE dicstandSort on  (dicstandSort.dic_type_code = SAR_STANDARDS_INFO.stand_sort  and dicstandSort.dic_id is not null and dicstandSort.valid_flag = 0 )
left join TS_DICTYPE dicstandState on  (dicstandState.dic_type_code = SAR_STANDARDS_INFO.stand_state  and dicstandState.dic_id is not null and dicstandState.valid_flag = 0 )
left join TS_DICTYPE dicstandNature on  (dicstandNature.dic_type_code = SAR_STANDARDS_INFO.stand_nature  and dicstandNature.dic_id is not null and dicstandNature.valid_flag = 0 )
left join TS_DICTYPE dicadoptExtent on  (dicadoptExtent.dic_type_code = SAR_STANDARDS_INFO.adopt_extent  and dicadoptExtent.dic_id is not null and dicadoptExtent.valid_flag = 0)
left join SAR_STAND_MENU ON SAR_STANDARDS_INFO.id = SAR_STAND_MENU.stand_id
where 1=1



        and stand_type = #{standType}




        and country = #{country}



        and stand_number like  concat(concat('%',#{standNumber}),'%')



        and stand_name  like  concat(concat('%',#{standName}),'%')



        and stand_state = #{standState}




        and stand_nature = #{standNature}



        and to_char(issue_time,'YYYY-MM-DD') >= #{issueTime1}


        and to_char(issue_time,'YYYY-MM-DD') <= #{issueTime2}



        and apply_arctic like concat(concat('%',#{applyArctic}),'%')



        and replace_stand_num like concat(concat('%',#{replaceStandNum}),'%')



        and replaced_stand_num like concat(concat('%',#{replacedStandNum}),'%')



        and SAR_STAND_MENU.MENU_ID in (
        select SAR_MENU.id from SAR_MENU start with id=#{menuId} connect by prior id= parent_id
        )



        and SAR_STAND_MENU.MENU_ID = (select SAR_MENU.id from SAR_MENU WHERE parent_id is null)



        and SAR_STANDARDS_INFO.id in

            #{item}

mapper.select:
select * from (select tmp_tb.* , rownum rn from (select , ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_STAND_VAL.property_type ) FROM SAR_STAND_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_STAND_VAL.property_val and TS_DICTYPE.dic_id is not null) WHERE SAR_STAND_VAL.stand_id = SAR_STANDARDS_INFO.id AND SAR_STAND_VAL.property_type = 'APPLY_ARCTIC' ) AS applyArcticShow, ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_STAND_VAL.property_type ) FROM SAR_STAND_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_STAND_VAL.property_val and TS_DICTYPE.dic_id is not null) WHERE SAR_STAND_VAL.stand_id = SAR_STANDARDS_INFO.id AND SAR_STAND_VAL.property_type = 'APPLY_AUTH' ) AS applyAuthShow, ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_STAND_VAL.property_type ) FROM SAR_STAND_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_STAND_VAL.property_val and TS_DICTYPE.dic_id is not null) WHERE SAR_STAND_VAL.stand_id = SAR_STANDARDS_INFO.id AND SAR_STAND_VAL.property_type = 'CATEGORY' ) AS categoryShow, ( SELECT listagg ( TS_DICTYPE.DIC_TYPE_NAME, ',' ) within GROUP ( ORDER BY SAR_STAND_VAL.property_type ) FROM SAR_STAND_VAL left join TS_DICTYPE on (TS_DICTYPE.dic_type_code = SAR_STAND_VAL.property_val and TS_DICTYPE.dic_id is not null) WHERE SAR_STAND_VAL.stand_id = SAR_STANDARDS_INFO.id AND SAR_STAND_VAL.property_type = 'ENERGY_KIND' ) AS emergyKindShow from SAR_STANDARDS_INFO
${pager.orderCondition}
) tmp_tb where rownum <= ${pager.endIndex}) where rn >= ${pager.startIndex}

select from SAR_STANDARDS_INFO where 1=1
and stand_type = #{standType}
