mapper.sql:
where 1=1


    and modify_date ${modifyDateOperator} #{modifyDate}


    and modify_date >= #{modifyDate1}


    and modify_date <= #{modifyDate2}


    and creation_time ${creationTimeOperator} #{creationTime}


    and creation_time >= #{creationTime1}


    and creation_time <= #{creationTime2}


    and valid_flag ${validFlagOperator} #{validFlag}


    and creation_user ${creationUserOperator} #{creationUser}


    and effective_date ${effectiveDateOperator} #{effectiveDate}


    and effective_date >= #{effectiveDate1}


    and effective_date <= #{effectiveDate2}


    and arctic_model ${arcticModelOperator} #{arcticModel}


    and ccie_num ${ccieNumOperator} #{ccieNum}


    and ccie_code ${ccieCodeOperator} #{ccieCode}


    and id ${idOperator} #{id}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from UT_CAR_CCC


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
