mapper.sql:
where 1=1


    and id ${idOperator} #{id}


    and corp_id ${corpIdOperator} #{corpId}


    and part_type ${partTypeOperator} #{partType}


    and part_name ${partNameOperator} #{partName}


    and part_nummber ${partNummberOperator} #{partNummber}


    and risk_grade ${riskGradeOperator} #{riskGrade}


    and suit_model ${suitModelOperator} #{suitModel}


    and moto_model ${motoModelOperator} #{motoModel}


    and moto_type ${motoTypeOperator} #{motoType}


    and oem_code ${oemCodeOperator} #{oemCode}


    and cert_number ${certNumberOperator} #{certNumber}


    and del_flag ${delFlagOperator} #{delFlag}


    and insert_time ${insertTimeOperator} #{insertTime}


    and insert_time >= #{insertTime1}


    and insert_time <= #{insertTime2}


    and update_time ${updateTimeOperator} #{updateTime}


    and update_time >= #{updateTime1}


    and update_time <= #{updateTime2}


    and corp_model ${corpModelOperator} #{corpModel}


    and part_sup ${partSupOperator} #{partSup}


    and qr_code_status ${qrCodeStatusOperator} #{qrCodeStatus}

mapper.select:
select  from
	(select tmp_tb.* , rownum rn from
		(select  from PART_PRODUCT


		 	${pager.orderCondition}

		) tmp_tb where rownum <= ${pager.endIndex})
		where rn >= ${pager.startIndex}
